import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { useRef, useEffect } from "react";

export default function Header({setIsPopUpForm}) {  
    const [isUserSelections, setIsUserSelections] = useState(false);
    let userRef = useRef();

    useEffect(() => {
        let handler = e => {
            if(!userRef.current.contains(e.target)) {
                setIsUserSelections(false);
            }
        }

        document.addEventListener('mousedown', handler);

        return () => {
            document.removeEventListener('mousedown', handler);
        }
    })

    function handleUserSelections() {
        setIsUserSelections(userSelections => !userSelections);
    }

    function handleSelection() {
        setIsUserSelections(false);
    }

    return (
        <div className="header">
            <div className="nav flex-box container">
                <div className="logo-container">
                    <NavLink to="/" exact="true"><img src="https://aster-imgs.s3.amazonaws.com/logo.png" alt="logo" /></NavLink>
                </div>
                <ul className="menu">
                    <li><NavLink to="/" exact="true">HOME</NavLink></li>
                    <li><NavLink to="/reservation" exact="true">RESERVATION</NavLink></li>
                    <li><NavLink to="/gallery" exact="true">GALLERY</NavLink></li>
                    {/* <li className="login-link" onClick={() => setIsPopUpForm(true)}>LOGIN</li> */}
                    <div className="user-drop-down" ref={userRef}>
                        <div className="user-btn" onClick={handleUserSelections}>
                            <p>MELONAIRE</p> <i className='bx bx-chevron-down'></i>
                        </div>
                        {isUserSelections ? 
                        <div className="user-selections flex-box">
                            <NavLink to="/profile" exact="true" onClick={handleSelection}><p>PROFILE</p></NavLink>
                            <NavLink to="/use_request" exact="true" onClick={handleSelection}><p>REQUEST</p></NavLink>
                            <p className="header-log-out">LOGOUT <i className='bx bx-log-out'></i> </p>
                        </div> 
                        : null}        
                    </div>
                </ul>
            </div>
        </div>
    )
}
