import { motion } from "framer-motion";
import React, { useState } from "react";

export default function LoginForm({setIsSignUpForm, popUpRef, setCurrentUser}) {
    const [loginInput, setLoginInput] = useState({
        username: "",
        password: ""
    })

    function handleLogin(e) {
        e.preventDefault();

        fetch('http://localhost:3000/login', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                user: loginInput,
            })
        })
        .then((r) => {
            if (r.ok) {
                r.json().then((data) => {
                    setCurrentUser
                })
            }
        })
    }

    function handleInput(e) {
        const name = e.target.name;
        let value = e.target.value;

        setLoginInput({
            ...loginInput,
            [name]: value,
        })
    }

    return (
        <motion.form
        initial={{ opacity: 0, y: 10 }} 
        whileInView={{ opacity: 1, y: 0 }} 
        transition={{ duration: .5 }}
        onSubmit={handleLogin} 
        className="form"
        ref={popUpRef}>
            <h1><span className="add-line-break">LOGIN TO</span> YOUR ACCOUNT.</h1>
            <div className="user-input">
                <p>User Name</p>
                <input type="text" placeholder="Enter User Name" name="username" onChange={handleInput}/>
            </div>

            <div className="user-input">
                <p>Password</p>
                <input type="password" placeholder="Enter Password" name="password" onChange={handleInput}/>
            </div>

            <div className="buttons-container flex-box">
                <button className="button" type="submit">
                    <p>SIGN IN</p>
                </button>
                <div className="sign-up-link flex-box" onClick={() => setIsSignUpForm(true)}>
                    <p>SIGN UP</p>
                    <div className="sign-up-arrow-container">
                        <img src="https://aster-imgs.s3.amazonaws.com/arrow-white.png" alt="arrow" />
                    </div>
                </div>
            </div>
        </motion.form>
    )
}
