import React, { useState } from "react";
import { motion } from "framer-motion";

export default function Account() {
    const [signUp, setSignUp] = useState(false);
    const [accounForm, setAccountForm] = useState(false);

    function handleSignUp(e) {
        e.preventDefault();
    }

    function handleLogin(e) {
        e.preventDefault();
    }

    return (
        <div className="account">
            {signUp ? 
            <motion.form onSubmit={handleSignUp}
            initial={{ opacity: 0, y: 10 }} 
            whileInView={{ opacity: 1, y: 0}} 
            transition={{ duration: .8, delay: .3 }} 
            viewport={{ once: true }}>
                <h1><span className="add-line-break">CREATE</span> YOUR ACCOUNT.</h1>
                <div className="user-input">
                    <p>User Name</p>
                    <input type="text" placeholder="Enter User Name" />
                </div>
                <div className="user-input">
                    <p>Password</p>
                    <input type="password" placeholder="Enter Password" />
                </div>
                <div className="user-input">
                    <p>Password Confirmation</p>
                    <input type="password" placeholder="Confirm Password" />
                </div>
                <div className="buttons-container flex-box">
                    <button className="button" type="submit">
                        <p>SIGN UP</p>
                    </button>
                    <div className="sign-up-link flex-box" onClick={() => setSignUp(false)}>
                        <p>GO BACK</p>
                        <div className="sign-up-arrow-container">
                            <img src="https://aster-imgs.s3.amazonaws.com/arrow-white.png" alt="arrow" />
                        </div>
                    </div>
                </div>
            </motion.form>
            : <motion.form onSubmit={handleLogin}
                    initial={{ opacity: 0, y: 10 }} 
                    whileInView={{ opacity: 1, y: 0}} 
                    transition={{ duration: .8, delay: .3 }} 
                    viewport={{ once: true }}>
                <h1><span className="add-line-break">LOGIN TO</span> YOUR ACCOUNT.</h1>
                <div className="user-input">
                    <p>User Name</p>
                    <input type="text" placeholder="Enter User Name" />
                </div>
                <div className="user-input">
                    <p>Password</p>
                    <input type="password" placeholder="Enter Password" />
                </div>
                <div className="buttons-container flex-box">
                    <button className="button" type="submit">
                        <p>SIGN IN</p>
                    </button>
                    <div className="sign-up-link flex-box" onClick={() => setSignUp(true)}>
                        <p>SIGN UP</p>
                        <div className="sign-up-arrow-container">
                            <img src="https://aster-imgs.s3.amazonaws.com/arrow-white.png" alt="arrow" />
                        </div>
                    </div>
                </div>
            </motion.form>}
        </div>
    )
}
