            [name]: value,
        })
    }

    return (
        <div className="pop-up-forms">
            {isInformationForm ? 
            <InformationForm popUpRef={popUpRef} currentUser={currentUser} setCurrentUser={setCurrentUser}/>
            :
            isSignUpForm ? 
            <SignUpForm popUpRef={popUpRef} setIsSignUpForm={setIsSignUpForm} setIsInformationForm={setIsInformationForm} setCurrentUser={setCurrentUser}/>
            : 
            <LoginForm popUpRef={popUpRef} setIsSignUpForm={setIsSignUpForm} />}
        </div>
    )
}
