import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import { motion } from "framer-motion";

const hiddenMask = `repeating-linear-gradient(to right, rgba(0,0,0,0) 0px, rgba(0,0,0,0) 50px, rgba(0,0,0,1) 50px, rgba(0,0,0,1) 50px)`;
const visibleMask = `repeating-linear-gradient(to right, rgba(0,0,0,0) 0px, rgba(0,0,0,0) 0px, rgba(0,0,0,1) 0px, rgba(0,0,0,1) 50px)`;

export default function Login() {
    const [isInView, setIsInView] = useState(false);

    return (
        <div className="background-color">
        <div className="login flex-box container">
            <div className="login-container flex-box">
                <div className="logo-container">
                    <NavLink to="/" exact="true"><img src="https://aster-imgs.s3.amazonaws.com/logo.png" alt="logo" /></NavLink>
                </div>

                <form>
                    <div className="user-input">
                        <p>User Name</p>
                        <input type="text" placeholder="Enter User Name" />
                    </div>
                    <div className="user-input">
                        <p>Password</p>
                        <input type="password" placeholder="Enter Password" />
                    </div>
                    <div className="buttons-container flex-box">
                        <div className="button">
                            <p>SIGN IN</p>
                            <div className="button-arrow-container">
                                <img src="https://aster-imgs.s3.amazonaws.com/arrow-white.png" alt="arrow" />
                            </div>
                        </div>
                        <NavLink to="/signup" exact="true" className="sign-up-link flex-box">
                            <p>SIGN UP</p>
                            <div className="sign-up-arrow-container">
                                <img src="https://aster-imgs.s3.amazonaws.com/arrow-charcoal.png" alt="arrow" />
                            </div>
                        </NavLink>
                    </div>
                </form>

                <div className="sphere-container">
                    <img src="https://aster-imgs.s3.amazonaws.com/wire-sphere.png" alt="sphere" />
                </div>
            </div>

            <motion.div initial={false}
                animate={
                isInView
                    ? { WebkitMaskImage: visibleMask, maskImage: visibleMask }
                    : { WebkitMaskImage: hiddenMask, maskImage: hiddenMask }
                }
                transition={{ duration: 1, delay: .5}}
                viewport={{ once: true }}
                onViewportEnter={() => setIsInView(true)}
                 className="login-img-container">
                <img src="https://aster-imgs.s3.amazonaws.com/pexels-rodnae-productions-8474703.png" alt="image" className="center-img" />
            </motion.div>
        </div>
        </div>
    )
}
