import '../index.css';
import { Route, Routes } from "react-router-dom";
import Header from './Header';
import Home from './Home';
import { useState, useEffect } from 'react';
import PopUpForms from './PopUpForms';
import Profile from './Profile';
import Reservation from './Reservation';
import Gallery from './Gallery';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [isPopUpForms, setIsPopUpForms] = useState(false);
  const token = localStorage.getItem("jwt");

  useEffect(() => {
    if (token !== null) {
      fetch("http://localhost:3000/me", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(r => r.json())
      .then(data => {
        setCurrentUser(data.user)
        console.log(data.user)
      })
    }
   },[])

  return (
    <div className="App">
      {isPopUpForms ? <PopUpForms setIsPopUpForms={setIsPopUpForms} setCurrentUser={setCurrentUser}/> : null}
      <Header setIsPopUpForm={setIsPopUpForms} cure/>
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route exact path="/profile" element={<Profile />} />
        <Route exact path="/reservation" element={<Reservation />} />
        <Route exact path="/gallery" element={<Gallery />} />
      </Routes>
    </div>
  );
}

export default App;
