import React, { useEffect, useState } from "react";
import Pagination from "./Pagination";
import Footer from "./Footer";

export default function Gallery() {
    const [gallery, setGallery] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [imagesPerPage, setImagesPerPage] = useState(12);

    useEffect(() => {
        fetch('http://localhost:3000/galleries')
        .then((r) => r.json())
        .then((data) => {
            console.log(data);
            setGallery(data);
        })
    },[])

    const totalImages = gallery.length;
    const indexOfLastImage = currentPage * imagesPerPage;
    const indexOfFirstImage = indexOfLastImage - imagesPerPage;
    const currentImages = gallery.slice(indexOfFirstImage, indexOfLastImage);

    function handlePages() {

    }

    return (
        <div className="background-color container">
            <div className="gallery">
                <div className="gallery-headline-container flex-box">
                    {/* <div className="upload-link">
                        <p>SHARE YOURS +</p>
                    </div> */}
                    <div className="tag-charcoal">
                        <p>MOMENTS</p>
                    </div>

                    <div className="gallery-headline">
                        <h1>GALLERY</h1>

                    </div>
                </div>

                <div className="gallery-container">
                    {currentImages.map((image) => {
                        return <div className="gallery-img-container flex-box" key={image.id}>
                        <div className="gallery-img">
                            <img src={image.img_url} alt="image" className="center-img"/>
                        </div>
                    </div>
                    })}

                </div>
            </div>
            <Pagination totalImages={totalImages} imagesPerPage={imagesPerPage} currentPage={currentPage}/>
            <Footer />
        </div>
    )
}
