import React, { useState, useRef, useEffect } from "react";
import LoginForm from "./LoginForm";
import SignUpForm from "./SignUpForm";
import InformationForm from "./InformationForm";

export default function Account({setLogin}) {
    const [isSignUpForm, setIsSignUpForm] = useState(false);
    const [isInformationForm, setIsInformationForm] = useState(false);
    let popUpRef = useRef();

    useEffect(() => {
        let handler = e => {
            if(!popUpRef.current.contains(e.target)) {
                setLogin(false);
            }
        }

        document.addEventListener('mousedown', handler);

        return () => {
            document.removeEventListener('mousedown', handler);
        }
    })

    return (
        <div className="account">
            {isInformationForm ? 
            <InformationForm ref={useRef}/>
            :
            isSignUpForm ? 
            <SignUpForm ref={useRef} setIsSignUpForm={setIsSignUpForm} setIsInformationForm={setIsInformationForm}/>
            : 
            <LoginForm setIsSignUpForm={setIsSignUpForm} />}
        </div>
    )
}
