import React from "react";

export default function Pagination({totalImages, imagesPerPage, currentPage, handlePages}) {
    const pageNums = [];

    for (let i = 1; i <= Math.ceil(totalImages / imagesPerPage); i++) {
        pageNums.push(i);
    }

    return (
        <div className="pagination container">
            {currentPage ===  1 }
            {pageNums.map((num) => {
                return <p key={num} className={num === currentPage ? "num-focused" : null} onClick={() => handlePages(num)}>{num}</p>;
            })}
            <i className='bx bx-chevron-right' ></i>
        </div>
    )
}
