import React, { useState } from "react";
import { motion } from "framer-motion";
import { useRef } from "react";

export default function InformationForm({popUpRef}) {
    const [isGenderSelections, setIsGenderSelections] = useState(false);
    let genderRef = useRef();

    function handleGenderSelections() {
        setIsGenderSelections(genderSelections => !genderSelections);
    }

    useEffect(() => {
        let handler = e => {
            if(!popUpRef.current.contains(e.target)) {
                setIsGenderSelections(false);
            }
        }

        document.addEventListener('mousedown', handler);

        return () => {
            document.removeEventListener('mousedown', handler);
        }
    })

    return (
        <motion.form
        initial={{ opacity: 0, y: 10 }} 
        whileInView={{ opacity: 1, y: 0 }} 
        transition={{ duration: .5 }} 
        style={{width: "650px"}} ref={popUpRef}>
            <h1><span className="add-line-break">COMPLETE</span> YOUR INFORMATION.</h1>
            <div className="input-flex-container">
                <div className="user-input">
                    <p>First Name</p>
                    <input type="text" placeholder="Enter First Name" />
                </div>

                <div className="user-input">
                    <p>Last Name</p>
                    <input type="text" placeholder="Enter Last Name" />
                </div>
            </div>

            <div className="input-flex-container">
                <div className="user-input">
                    <p>Email</p>
                    <input type="text" placeholder="Enter Email" />
                </div>

                <div className="age-and-gender-container">
                    <div className="user-input">
                        <p>Age</p>
                        <input type="text" />
                    </div>

                    <div className="gender-dropdown">
                        <p>Gender</p>
                        <div className="gender-btn flex-box" onClick={handleGenderSelections}>
                            <p>Select</p>
                            <i className='bx bx-chevron-down'></i>
                        </div>
                        {isGenderSelections ? 
                        <div className="gender-selections flex-box" ref={drop}>
                            <p>Male</p>
                            <p>Female</p>
                            <p>Intersex</p>
                            <p>Trans</p>
                            <p>Non-Confirming</p>
                            <p style={{borderBottom: "none", paddingBottom: "0"}}>Others</p>
                        </div> 
                        : null}
                    </div>
                </div>
            </div>

            <div className="buttons-container">
                <button className="button" type="submit" style={{textAlign: "center"}}>
                    <p>CONFIRM</p>
                </button>
            </div>
        </motion.form> 
    )
}
