import React, { useEffect, useState } from "react";
import Footer from "./Footer";

export default function Gallery() {
    const [gallery, setGallery] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);


    useEffect(() => {
        fetch('http://localhost:3000/galleries')
        .then((r) => r.json())
        .then((data) => {
            console.log(data);
            setGallery(data);
        })
    },[])


    console.log(gallery)

    return (
        <div className="background-color container">
            <div className="gallery">
                <div className="gallery-headline-container flex-box">
                    {/* <div className="upload-link">
                        <p>SHARE YOURS +</p>
                    </div> */}
                    <div className="tag-charcoal">
                        <p>MOMENTS</p>
                    </div>

                    <div className="gallery-headline">
                        <h1>GALLERY</h1>

                    </div>
                </div>

                <div className="gallery-container">
                    {gallery.map((image) => {
                        return <div className="gallery-img-container flex-box" key={image.id}>
                        <div className="gallery-img">
                            <img src={image.img_url} alt="image" className="center-img"/>
                        </div>
                    </div>
                    })}

                </div>
            </div>
            <Footer />
        </div>
    )
}
