import React, { useState, useRef, useEffect } from "react";
import LoginForm from "./LoginForm";
import SignUpForm from "./SignUpForm";

export default function Account({setLogin}) {
    const [signUp, setSignUp] = useState(false);
    const [, setAccountForm] = useState(false);
    let popUpRef = useRef();

    useEffect(() => {
        let handler = e => {
            if(!popUpRef.current.contains(e.target)) {
                setLogin(false);
            }
        }

        document.addEventListener('mousedown', handler);

        return () => {
            document.removeEventListener('mousedown', handler);
        }
    })

    return (
        <div className="account">
            {accountForm ? 
            <form ref={popUpRef} style={{width: "650px"}}>
                <h1><span className="add-line-break">COMPLETE</span> YOUR INFORMATION.</h1>
                <div className="input-flex-container">
                    <div className="user-input">
                        <p>First Name</p>
                        <input type="text" placeholder="Enter First Name" />
                    </div>

                    <div className="user-input">
                        <p>Last Name</p>
                        <input type="password" placeholder="Enter Last Name" />
                    </div>
                </div>

                <div className="input-flex-container">
                    <div className="user-input">
                        <p>Email</p>
                        <input type="text" placeholder="Enter Email" />
                    </div>

                    <div className="input-flex-container">
                        <div className="user-input">
                            <p>Age</p>
                            <input type="text" />
                        </div>

                        <div className="user-input">
                            <p>Gender</p>
                            <input type="text" />
                        </div>
                    </div>
                </div>

                <div className="buttons-container">
                    <button className="button" type="submit" style={{textAlign: "center"}}>
                        <p>CONFIRM</p>
                    </button>
                </div>
            </form> 
            :
            signUp ? 
            <SignUpForm setSignUp={setSignUp} />
            : 
            <LoginForm setSignUp={setSignUp} />}
        </div>
    )
}
