import React from "react";

export default function Pagination({totalImages, imagesPerPage, currentPage, handlePages}) {
    const pageNums = [];

    for (let i = 1; i <= Math.ceil(totalImages / imagesPerPage); i++) {
        pageNums.push(i);
    }

    const lastNum = pageNums.slice(-1);

    return (
        <div className="pagination container">
            {currentPage ===  1 ? null :<i className='bx bx-chevron-left'></i>}
            {pageNums.map((num) => {
                return <a href="#gallery" key={num} className={num === currentPage ? "num-focused" : null} onClick={() => handlePages(num)}>{num}</a>;
            })}
            {currentPage === pageNums.slice(-1) ? null : <i className='bx bx-chevron-right' ></i>}
        </div>
    )
}
